import React from "react";
import PropTypes from "prop-types";
import styles from "./Arrow.module.css";

export default function Arrow({ deg }) {
  let svg = document.querySelector(".svgImg");

  svg &&
    svg.addEventListener("change", function () {
      console.log(svg);
      svg.style.setProperty("--rotate", svg.deg);
    });

  return (
    <svg
      className={styles.svgImg}
      width="20px"
      height="20px"
      viewBox="0 0 16 16"
      // transform={`rotate(${deg})`}
    >
      <path
        d="M11.76 4.3L8.1 11.77 8 6.26l.45-.24 3.31-1.72M14 2L7.99 5.13 2 2.12 8.13 14 14 2z"
        fill="#eb6e4b"
      />
    </svg>
  );
}

Arrow.propTypes = {
  deg: PropTypes.number.isRequired,
};
